mapper.insert:
insert into users (
	userId
	, password
)
values (
	#{userId}
	, #{password}
)

insert into itemhaveinfo (
	characterid,
	itemid,
	quantity
	)
	values(
	#{characterid},
	#{itemid},
	#{quantity}
	)

insert into itemequiphaveinfo(
	itemequipid,
	characterid,
	itemid
	)
	values(
	itemequipidseq.nextval,
	#{characterid},
	#{itemid}
	)

